import React from "react";
import { BrowserRouter, Routes, Route , useNavigate } from 'react-router-dom';
import swal from 'sweetalert';

import Students from './Students';
import AddStudent from './AddStudent';
import EditStudent from './EditStudent';
import Register from "./Register";
import Login from "./Login";


function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
        {
          localStorage.getItem ?
                    (
                        <div>
                            <h2>
                                You Choosed Number <span style={{ color: "red" }}> {num} </span>
                            </h2><br />
                            <h2>
                                My Name is <span style={{ color: "red", textTransform: "uppercase" }}> {name} </span>
                            </h2><br />
                            <h2>
                                I Have  <span style={{ color: "red" }}> {moves} </span> Moves
                            </h2> </div>
                    )
                    :
                    <h2>Choose A Number From Select Box</h2>
            }
          if (localStorage.getItem(token)) {
          <Route path="/add-student" element={<AddStudent />} />
          }
          else
          {
            swal("Unauthoorized","You need to Login First","error") 
          }
          <Route path="/register" element={<Register />} />
          <Route path="/" element={<Login />} />
          <Route path="/students" element={<Students />} />
          <Route path="/edit-student/:id" element={<EditStudent />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
