import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
// import swal from 'sweetalert';

function AddStudent() {

    // const history = useHistory();
    const [student, setStudent] = useState({
        name: "",
        email: "",
        course: "",
        phone: "",
        error_list: [],
    });

    const handleInput = (e) => {
        const { name, value } = e.target;
        setStudent({ ...student, [name]: value });
    }

    const saveStudent = async (e) => {
        e.preventDefault();

        const data = {
            name: student.name,
            course: student.course,
            email: student.email,
            phone: student.phone,
        }

        const res = await axios.post(`http://localhost:8000/api/add-student`, data);
             if (res.data.status === 200) {
                // swal("Success!", res.data.message, "success");
                setStudent({
                    name: '',
                    course: '',
                    email: '',
                    phone: '',
                    error_list: [],
                });
                // history.push('/students');
            }
            else if (res.data.status === 422) {
                setStudent({ ...student, error_list: res.data.validate_err });
            }

        // axios.post(`/api/add-student`, data).then(res => {

        //     if (res.data.status === 200) {
        //         swal("Success!", res.data.message, "success");
        //         setStudent({
        //             name: '',
        //             course: '',
        //             email: '',
        //             phone: '',
        //             error_list: [],
        //         });
        //         history.push('/students');
        //     }
        //     else if (res.data.status === 422) {
        //         setStudent({ ...student, error_list: res.data.validate_err });
        //     }
        // });

        return (
            <>
                <div className="container">
                    <div className="row">
                        <div className="col-5 mx-auto">
                            <div className="card">
                                <div className="card-header">
                                    <h4>
                                        Add Student
                                        <Link className="btn btn-primary float-end" to={"/students"}>Go Back</Link>
                                    </h4>
                                </div>
                                <div className="card-body">
                                    <form onSubmit={saveStudent}>
                                        <div className="form-group mb-3">
                                            <label className="pr-3">Name</label> <br />
                                            <input type="text" className="form-item form-control" onChange={handleInput} name="name" value={student.name} />
                                        </div>
                                        <div className="form-group mb-3">
                                            <label>Email </label>
                                            <input type="text" className="form-control" onChange={handleInput} name="email" value={student.email} />
                                        </div>
                                        <div className="form-group mb-3">
                                            <label>Course </label>
                                            <input type="text" className="form-control" onChange={handleInput} name="course" value={student.course} />
                                        </div>
                                        <div className="form-group mb-3">
                                            <label>Phone </label>
                                            <input type="text" className="form-control" onChange={handleInput} name="phone" value={student.phone} />
                                        </div>
                                        <div className="form-group mb-3">
                                            <button type="submit" className="btn btn-success float-end">Save</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

    export default AddStudent;
