import React, { useState } from "react";
import { useNavigate} from "react-router-dom";
import axios from 'axios';
import swal from 'sweetalert';

function Login() {

    const navigate = useNavigate();

    const [credentials, setCredentials] = useState({
        email: "",
        password: "",
        error_list: [],
    });

    const handleInput = (e) => {
        const { name, value } = e.target;
        setCredentials({ ...credentials, [name]: value });
    }

       const authenticate = async (e) => {
        e.preventDefault();

        const data = {
            email: credentials.email,
            password: credentials.password,
        }

        // const res = await axios.post(`http://127.0.0.1:8000/api/login` ,
        // {headers:{'Content-Type' : 'applicaton/json'}},
        // {'credentials' : 'include'},
        // {body: JSON.stringify({data})});

        // const res = await axios.post(`http://127.0.0.1:8000/api/login` , data);

            const res = await fetch('http://localhost:8000/api/login', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({data})
        });
            console.log(res);
             if (res.status === 200) {
                // swal("Success!", res.data.message, "success");
                 swal({
                        title: "Success!",
                        text: "Student record is added Successfully",
                        icon: "success",
                        timer: 2000,
                        button: false
                    })
                setCredentials({
                    email: '',
                    password: '',
                    error_list: [],
                });
                navigate('/students');
            }
            else if (res.data.status === 422) {
                swal("All fields are mandetory","","error");
                setCredentials({ ...credentials, error_list: res.data.validate_err });
            }
        }

        // axios.post(`/api/add-student`, data).then(res => {

        //     if (res.data.status === 200) {
        //         swal("Success!", res.data.message, "success");
        //         setStudent({
        //             name: '',
        //             course: '',
        //             email: '',
        //             phone: '',
        //             error_list: [],
        //         });
        //         history.push('/students');
        //     }
        //     else if (res.data.status === 422) {
        //         setStudent({ ...student, error_list: res.data.validate_err });
        //     }
        // });
        return (
            <>
                <div className="container">
                    <div className="row">
                        <div className="col-5 mx-auto">
                            <div className="card">
                                <div className="card-header">
                                    <h4>
                                        Login
                                    </h4>
                                </div>
                                <div className="card-body">
                                    <form onSubmit={authenticate}>
                                        <div className="form-group mb-3">
                                            <label>Email </label>
                                            <input type="email" className="form-control" onChange={handleInput} name="email" value={credentials.email} />
                                            <span className="text-danger">{credentials.error_list.email}</span>
                                        </div>
                                        <div className="form-group mb-3">
                                            <label>Password </label>
                                            <input type="password" className="form-control" onChange={handleInput} name="password" value={credentials.password} />
                                            <span className="text-danger">{credentials.error_list.password}</span>
                                        </div>
                                        <div className="form-group mb-3">
                                            <button type="submit" className="btn btn-success float-end">Save</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
}

    export default Login;
