import React, { useState } from "react";
import { useNavigate} from "react-router-dom";
import axios from 'axios';
import swal from 'sweetalert';

function Login() {

    const navigate = useNavigate();

    const [user, setUser] = useState({
        email: "",
        password: "",
        error_list: [],
    });

    const handleInput = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    }

    const registerUser = async (e) => {
        e.preventDefault();

        const data = {
            email: user.email,
            password: user.password,
        }

        const res = await axios.post(`http://127.0.0.1:8000/api/login`, data);
             if (res.data.status === 200) {
                // swal("Success!", res.data.message, "success");
                 swal({
                        title: "Success!",
                        text: "User Registered Successfully",
                        icon: "success",
                        timer: 2000,
                        button: false
                    })
                setUser({
                    email: '',
                    password: '',
                    error_list: [],
                });
                navigate('/');
            }
            else if (res.data.status === 422) {
                // swal("All fields are mandetory","","error");
                setUser({ ...user, error_list: res.data.validate_err });
            }
        }
        // axios.post(`/api/add-student`, data).then(res => {

        //     if (res.data.status === 200) {
        //         swal("Success!", res.data.message, "success");
        //         setStudent({
        //             name: '',
        //             course: '',
        //             email: '',
        //             phone: '',
        //             error_list: [],
        //         });
        //         history.push('/students');
        //     }
        //     else if (res.data.status === 422) {
        //         setStudent({ ...student, error_list: res.data.validate_err });
        //     }
        // });
        return (
            <>
                <div className="container">
                    <div className="row">
                        <div className="col-5 mx-auto">
                            <div className="card">
                                <div className="card-header">
                                    <h4>
                                        Sign Up
                                    </h4>
                                </div>
                                <div className="card-body">
                                    <form onSubmit={registerUser}>
                                        <div className="form-group mb-3">
                                            <label>Email </label>
                                            <input type="email" className="form-control" onChange={handleInput} name="email" value={user.email} />
                                            <span className="text-danger">{user.error_list.email}</span>
                                        </div>
                                        <div className="form-group mb-3">
                                            <label>Password </label>
                                            <input type="password" className="form-control" onChange={handleInput} name="password" value={user.password} />
                                            <span className="text-danger">{user.error_list.password}</span>
                                        </div>
                                        <div className="form-group mb-3">
                                            <button type="submit" className="btn btn-success float-end">Save</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
}

    export default Login;
